A = (1,2);
B = (0,0);
C = (3,0);
O = circumcenter(A,B,C);

draw(A--B--C--cycle);
draw(circumcircle(A,B,C));
draw(B--O);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
dot("$O$", O, NE);
[/asy]
